mapper.select:
SELECT * FROM Branch
branchName LIKE concat(concat('%', #{branchSearchVo.branchName}), '%') OR branchShortName LIKE concat(concat('%', #{branchSearchVo.branchName}), '%')
LIMIT #{branchSearchVo.start}, #{branchSearchVo.size}

mapper.update:
UPDATE Branch


        branchName = #{branchEditVo.branchName},


        branchShortName = #{branchEditVo.branchShortName}


WHERE branchId = #{branchEditVo.branchId}
